import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { carsAPI } from '../services/api';
import './Home.css';

const Home = () => {
  const [featuredCars, setFeaturedCars] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchFeaturedCars();
  }, []);

  const fetchFeaturedCars = async () => {
    try {
      const response = await carsAPI.getAll({ featured: 'true' });
      setFeaturedCars(response.data.slice(0, 3));
    } catch (error) {
      console.error('Error fetching featured cars:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home">
      <section className="hero">
        <div className="container">
          <h1>Find Your Perfect Car</h1>
          <p>Rent premium cars at affordable prices</p>
          <Link to="/cars" className="btn btn-primary btn-large">
            Browse Cars
          </Link>
        </div>
      </section>

      <section className="featured-cars container">
        <h2>Featured Cars</h2>
        {loading ? (
          <p>Loading...</p>
        ) : featuredCars.length > 0 ? (
          <div className="card-grid">
            {featuredCars.map((car) => (
              <div key={car._id} className="car-card">
                <img src={car.images[0] || 'https://via.placeholder.com/300'} alt={car.name} />
                <div className="car-card-content">
                  <h3>{car.name}</h3>
                  <p>{car.brand} • {car.model} • {car.year}</p>
                  <p>{car.location}</p>
                  <div className="price">${car.pricePerDay}/day</div>
                  <Link to={`/cars/${car._id}`} className="btn btn-primary">
                    View Details
                  </Link>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p>No featured cars available</p>
        )}
      </section>
    </div>
  );
};

export default Home;
