import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const carouselRef = useRef(null);
  const images = [
    'https://tse4.mm.bing.net/th/id/OIP.Tib7YSPTssbNeaKIs-HWbgHaE7?pid=Api&P=0&h=180',
    'https://tse3.mm.bing.net/th/id/OIP.eUpF5oT_8mo8Lgmckm2cuAHaEK?pid=Api&P=0&h=180',
    'https://tse4.mm.bing.net/th/id/OIP.ZUxsKe6kIn_HbIGTzuWx_AHaDI?pid=Api&P=0&h=180'
  ];

  useEffect(() => {
    const carousel = carouselRef.current;
    if (!carousel) return;

    let scrollPosition = 0;
    const scrollSpeed = 0.5; // pixels per frame (slower for smoother effect)
    let animationId;

    const scroll = () => {
      const containerWidth = carousel.parentElement.offsetWidth;
      const totalWidth = containerWidth * images.length;
      
      scrollPosition += scrollSpeed;
      
      // Reset scroll position when we've scrolled past the first set of images
      if (scrollPosition >= totalWidth) {
        scrollPosition = 0;
      }
      
      carousel.style.transform = `translateX(-${scrollPosition}px)`;
      animationId = requestAnimationFrame(scroll);
    };

    // Start the infinite scroll after a short delay
    const timeoutId = setTimeout(() => {
      animationId = requestAnimationFrame(scroll);
    }, 100);

    return () => {
      clearTimeout(timeoutId);
      if (animationId) {
        cancelAnimationFrame(animationId);
      }
    };
  }, [images.length]);

  return (
    <div className="home">
      <section className="hero">
        <div className="container">
          <h1>Find Your Perfect Car</h1>
          <p>Rent premium cars at affordable prices</p>
          <Link to="/cars" className="btn btn-primary btn-large">
            Browse Cars
          </Link>
        </div>
      </section>

      <section className="image-carousel-section">
        <div className="image-carousel-container">
          <div className="image-carousel" ref={carouselRef}>
            {images.map((img, index) => (
              <img
                key={index}
                src={img}
                alt={`Car ${index + 1}`}
                className="carousel-image"
              />
            ))}
            {/* Duplicate images for seamless infinite scroll */}
            {images.map((img, index) => (
              <img
                key={`duplicate-${index}`}
                src={img}
                alt={`Car ${index + 1}`}
                className="carousel-image"
              />
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
