import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const images = [
    'https://tse4.mm.bing.net/th/id/OIP.Tib7YSPTssbNeaKIs-HWbgHaE7?pid=Api&P=0&h=180',
    'https://tse3.mm.bing.net/th/id/OIP.eUpF5oT_8mo8Lgmckm2cuAHaEK?pid=Api&P=0&h=180',
    'https://tse4.mm.bing.net/th/id/OIP.ZUxsKe6kIn_HbIGTzuWx_AHaDI?pid=Api&P=0&h=180'
  ];

  // Create multiple sets of images for seamless infinite scroll
  const allImages = [...images, ...images, ...images];

  return (
    <div className="home">
      <section className="hero">
        <div className="container">
          <h1>Find Your Perfect Car</h1>
          <p>Rent premium cars at affordable prices</p>
          <Link to="/cars" className="btn btn-primary btn-large">
            Browse Cars
          </Link>
        </div>
      </section>

      <section className="image-carousel-section">
        <div className="image-carousel-container">
          <div className="image-carousel">
            {allImages.map((img, index) => (
              <img
                key={index}
                src={img}
                alt={`Car ${(index % images.length) + 1}`}
                className="carousel-image"
              />
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
