import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react';
import './Payment.css';

const Payment = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [paymentMethod, setPaymentMethod] = useState('upi');
  const [paymentStatus, setPaymentStatus] = useState(null);

  // Get booking details from location state
  const bookingData = location.state?.bookingData || {
    _id: 'BOOK123',
    totalPrice: 5000,
    car: { name: 'Sample Car' },
    startDateTime: new Date().toLocaleDateString(),
    endDateTime: new Date().toLocaleDateString(),
  };

  const displayData = {
    bookingId: bookingData._id || 'BOOK123',
    totalPrice: bookingData.totalPrice || 5000,
    carName: bookingData.car?.name || 'Sample Car',
    startDate: new Date(bookingData.startDateTime).toLocaleDateString(),
    endDate: new Date(bookingData.endDateTime).toLocaleDateString(),
  };

  const upiId = '7207096263@ybl';
  const upiLink = `upi://pay?pa=${upiId}&pn=CarRental&am=${displayData.totalPrice}&tn=Booking%20Payment`;

  const handlePaymentSuccess = () => {
    setPaymentStatus('success');
    setTimeout(() => {
      navigate('/bookings', { 
        state: { message: 'Payment completed successfully!' } 
      });
    }, 2000);
  };

  const handleCancel = () => {
    navigate(-1);
  };

  return (
    <div className="payment-container">
      <div className="payment-wrapper">
        <h1>Payment Gateway</h1>
        
        <div className="payment-details-card">
          <h2>Booking Summary</h2>
          <div className="booking-info">
            <div className="info-row">
              <span className="label">Booking ID:</span>
              <span className="value">{bookingData.bookingId}</span>
            </div>
            <div className="info-row">
              <span className="label">Car:</span>
              <span className="value">{bookingData.carName}</span>
            </div>
            <div className="info-row">
              <span className="label">Start Date:</span>
              <span className="value">{bookingData.startDate}</span>
            </div>
            <div className="info-row">
              <span className="label">End Date:</span>
              <span className="value">{bookingData.endDate}</span>
            </div>
            <div className="info-row total">
              <span className="label">Total Amount:</span>
              <span className="value">₹{bookingData.totalPrice}</span>
            </div>
          </div>
        </div>

        <div className="payment-methods">
          <h2>Select Payment Method</h2>
          
          <div className="method-tabs">
            <button 
              className={`tab-button ${paymentMethod === 'upi' ? 'active' : ''}`}
              onClick={() => setPaymentMethod('upi')}
            >
              UPI
            </button>
            <button 
              className={`tab-button ${paymentMethod === 'card' ? 'active' : ''}`}
              onClick={() => setPaymentMethod('card')}
            >
              Debit/Credit Card
            </button>
            <button 
              className={`tab-button ${paymentMethod === 'netbanking' ? 'active' : ''}`}
              onClick={() => setPaymentMethod('netbanking')}
            >
              Net Banking
            </button>
          </div>

          {paymentStatus === 'success' && (
            <div className="success-message">
              ✓ Payment Successful! Redirecting...
            </div>
          )}

          {paymentMethod === 'upi' && paymentStatus !== 'success' && (
            <div className="upi-payment">
              <h3>Pay via UPI</h3>
              <p className="upi-instruction">Scan the QR code below with your UPI app or enter UPI ID manually</p>
              
              <div className="qr-container">
                <QRCodeSVG 
                  value={upiLink} 
                  size={250}
                  level="H"
                  includeMargin={true}
                  fgColor="#000000"
                  bgColor="#ffffff"
                />
              </div>

              <div className="upi-id-section">
                <p className="upi-label">UPI ID:</p>
                <div className="upi-id-display">
                  <span>{upiId}</span>
                  <button 
                    className="copy-btn"
                    onClick={() => {
                      navigator.clipboard.writeText(upiId);
                      alert('UPI ID copied to clipboard!');
                    }}
                  >
                    Copy
                  </button>
                </div>
              </div>

              <div className="payment-instructions">
                <h4>Payment Instructions:</h4>
                <ol>
                  <li>Open your UPI app (Google Pay, PhonePe, BHIM, etc.)</li>
                  <li>Scan the QR code or enter the UPI ID</li>
                  <li>Enter amount: ₹{displayData.totalPrice}</li>
                  <li>Complete the payment with your PIN</li>
                  <li>Confirmation will appear here automatically</li>
                </ol>
              </div>

              <button 
                className="btn btn-success"
                onClick={handlePaymentSuccess}
              >
                ✓ Payment Completed
              </button>
            </div>
          )}

          {paymentMethod === 'card' && paymentStatus !== 'success' && (
            <div className="card-payment">
              <h3>Debit/Credit Card Payment</h3>
              <form className="card-form">
                <div className="form-group">
                  <label>Card Holder Name *</label>
                  <input type="text" placeholder="Enter your name" required />
                </div>

                <div className="form-group">
                  <label>Card Number *</label>
                  <input type="text" placeholder="1234 5678 9012 3456" maxLength="19" required />
                </div>

                <div className="form-row">
                  <div className="form-group">
                    <label>Expiry Date *</label>
                    <input type="text" placeholder="MM/YY" maxLength="5" required />
                  </div>
                  <div className="form-group">
                    <label>CVV *</label>
                    <input type="text" placeholder="123" maxLength="3" required />
                  </div>
                </div>

                <button 
                  type="button"
                  className="btn btn-success"
                  onClick={handlePaymentSuccess}
                >
                  Pay ₹{bookingData.totalPrice}
                </button>
              </form>
            </div>
          )}

          {paymentMethod === 'netbanking' && paymentStatus !== 'success' && (
            <div className="netbanking-payment">
              <h3>Net Banking Payment</h3>
              <div className="bank-list">
                <button className="bank-option">HDFC Bank</button>
                <button className="bank-option">ICICI Bank</button>
                <button className="bank-option">State Bank of India</button>
                <button className="bank-option">Axis Bank</button>
                <button className="bank-option">Other Banks</button>
              </div>
              <button 
                className="btn btn-success"
                onClick={handlePaymentSuccess}
              >
                Pay ₹{displayData.totalPrice}
              </button>
            </div>
          )}
        </div>

        <button 
          className="btn btn-secondary cancel-btn"
          onClick={handleCancel}
        >
          Cancel Payment
        </button>
      </div>
    </div>
  );
};

export default Payment;
